class TranslationHindi extends StatefulWidget {
  @override
  _TranslationHindiState createState() => _TranslationHindiState();
}

class _TranslationHindiState extends State<TranslationHindi> {
  TextEditingController translateText = TextEditingController();
  String data = "";

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: SafeArea(
        child: Column(
          children: [
            Padding(
              padding: const EdgeInsets.all(8.0),
              child: Text(
                "Translation",
                style: TextStyle(
                  fontWeight: FontWeight.w600,
                  fontSize: 32,
                ),
              ),
            ),
            Container(
              margin: EdgeInsets.all(12),
              height: 10 * 24.0,
              child: TextField(
                controller: translateText,
                maxLines: 10,
                decoration: InputDecoration(
                  hintText: "Enter a message",
                  fillColor: Colors.grey[300],
                  filled: true,
                ),
              ),
            ),
            Row(
              mainAxisAlignment: MainAxisAlignment.center,
              children: [
                RaisedButton(
                  child: Text("Translate"),
                  color: Colors.purple[300],
                  textColor: Colors.white,
                  onPressed: () async {
                    final translator = GoogleTranslator();
                    final text = await translator.translate(translateText.text,
                        to: 'hi');
                    setState(() {
                      data = text.toString();
                    });
                  },
                ),
                RaisedButton(
                  child: Text("Clear"),
                  color: Colors.red,
                  textColor: Colors.white,
                  onPressed: () {
                    setState(() {
                      this.translateText.text = "";
                    });
                  },
                ),
              ],
            ),
            Expanded(
              flex: 1,
              child: SingleChildScrollView(
                scrollDirection: Axis.vertical,
                child: Padding(
                  padding: const EdgeInsets.all(16.0),
                  child: Text(
                    data,
                    style: TextStyle(
                      fontSize: 17,
                    ),
                  ),
                ),
              ),
            )
          ],
        ),
      ),
    );
  }
}

class GrammarList extends StatefulWidget {
  @override
  _GrammarListState createState() => _GrammarListState();
}

class _GrammarListState extends State<GrammarList> {
  List<String> topics = [
    "व्याकरण (Grammar) ",
    "वर्ण-विचार वर्ण, वर्णमाला की  परिभाषा",
    "Shabad Vichar(Etymology)(शब्द विचार)",
    "वाक्य विचार(Syntax) ",
    "Sangya(Noun)(संज्ञा)",
    "Sarvnam(Pronoun)(सर्वनाम)",
    "Kriya(Verb)(क्रिया)",
    "Kal(Tense)(काल)",
    "Alankaar(Figure of speech)अलंकार",
    "Vachan(Number)(वचन)",
    "Samas(Compound)(समास)",
    "   Muhavare(Idioms)( मुहावरे)",
    "   Pratyaya(Suffix)(प्रत्यय)",
    "   कारक (Case) की परिभाषा",
    "   Sandhi (Seam)-संधि"
  ];

  List<String> topicsAnswers = [
    "<h3>व्याकरण (Grammar) की परिभाषा </h3>    <p>व्याकरण- व्याकरण वह विद्या है जिसके द्वारा हमे किसी भाषा का शुद्ध बोलना, लिखना एवं समझना आता है। </p>   भाषा की संरचना के ये नियम सीमित होते हैं और भाषा की अभिव्यक्तियाँ असीमित। एक-एक नियम असंख्य अभिव्यक्तियों को नियंत्रित करता है।    भाषा के इन नियमों को एक साथ जिस शास्त्र के अंतर्गत अध्ययन किया जाता है उस शास्त्र को व्याकरण कहते हैं। </p>    <p>व्यक्ति और स्थान-भेद से भाषा में अंतर आ सकता है। इस प्रकार किसी भाषा का रूप निश्चित नहीं रहता। अज्ञान अथवा      भ्रम के कारण कुछ लोग शब्दों के उच्चारण अथवा अर्थ-ग्रहण में गलती करते हैं। इस प्रकार भाषा का रूप विकृत हो जाता है।       भाषा की शुद्धता और एकरूपता बनाए रखना ही व्याकरण का कार्य है। </p>  <p> वस्तुतः व्याकरण भाषा के नियमों का संकलन और विश्लेषण करता है और इन नियमों को स्थिर करता है। व्याकरण के ये नियम भाषा को  मानक एवं परिनिष्ठित बनते हैं। व्याकरण स्वयं भाषा के नियम नहीं बनाता। एक भाषाभाषी समाज के लोग भाषा के जिस रूप का प्रयोग करते हैं,  उसी को आधार मानकर वैयाकरण व्याकरणिक नियमों को निर्धारित करता है। अतः यह कहा जा सकता है कि-</p>  <p><b>व्याकरण उस  शास्त्र को कहते हैं जिसमें किसी भाषा के शुद्ध रूप का ज्ञान कराने वाले नियम बताए गए हों।  </b></p> <p> कुछ उदाहरण देखें-<br>  (1)  सीता पेड़ पर चढ़ता है। <br> (2) हम सभी जाएगा। <br> पहले वाक्य में यह अशुद्धि है कि सीता स्त्रीलिंग के साथ क्रिया का रूप 'चढ़ती' होना चाहिए। वाक्य बनेगा - सीता पेड़ पर चढ़ती  है।  वाक्य संख्या 2 में कर्ता बहुवचन है, अतः वाक्य बनेगा - हम सभी जाएँगे। ये अशुद्धियाँ क्रिया-संबंधी हैं। </p> <p> अन्य उदाहरण देखिए-<br> मार दिया को ने राम श्याम। <br> इस वाक्य से अर्थ स्पष्ट नहीं होता क्योंकि कर्ता, कर्म तथा कारक निश्चित स्थान पर नहीं हैं। 'राम ने श्याम को मार दिया।'  वाक्य का अर्थ 'श्याम ने राम को मार दिया।' वाक्य के अर्थ से भिन्न है। वक्ता जो बात कहना चाहता है, उसे वाक्य में   शब्दों का विन्यास उसके अनुरूप रखना होगा। इनसे संबंधित नियम हिन्दी व्याकरण में उल्लिखित हैं। </p>   <h3> व्याकरण के अंग </h3> <p>भाषा के चार मुख्य अंग हैं-  वर्ण, शब्द पद और वाक्य। इसलिए व्याकरण के मुख्यतः चार विभाग हैं-<br> (1) वर्ण-विचार<br>(2) शब्द-विचार<br>(3) पद-विचार<br>(4) वाक्य विचार</p> <p><b><u>  (1) वर्ण विचार  या अक्षर:-</b></u> भाषा की उस छोटी ध्वनि (इकाई) को वर्ण कहते है जिसके टुकड़े नही किये सकते है। <br>   जैसे- अ, ब, म, क, ल, प आदि।</p>  <p> इसमें वर्णमाला, वर्णों के भेद, उनके उच्चारण, प्रयोग तथा संधि पर विचार किया जाता है। </p> <p><b><u>  (2) शब्द-विचार:- </b></u> वर्णो के उस मेल को शब्द कहते है जिसका कुछ अर्थ होता है।<br> जैसे- कमल, राकेश, भोजन, पानी, कानपूर आदि। </p> <p> इसमें शब्द-रचना, उनके भेद, शब्द-सम्पदा तथा उनके प्रयोग आदि पर विचार किया जाता है। </p>  <p><b><u> (3) पद-विचार:-</b></u> इसमें पद-भेद, पद-रूपान्तर तथा उनके प्रयोग आदि पर विचार किया जाता है। </p> <p><b><u>  (4) वाक्य-विचार:-</b></u> अनेक शब्दों को मिलाकर वाक्य बनता है। ये शब्द मिलकर किसी अर्थ का ज्ञान कराते है।<br> जैसे- सब घूमने जाते है।<br> राजू सिनेमा देखता है। </p> <p> इनमें वाक्य व उसके अंग, पदबंध तथा विराम चिह्न आदि पर विचार किया जाता है। </p>  <h3>हिन्दी व्याकरण की विशेषताएँ </h3> <p>  हिन्दी-व्याकरण संस्कृत व्याकरण पर आधृत होते हुए भी अपनी कुछ स्वतंत्र विशेषताएँ रखता है। हिन्दी को संस्कृत का उत्तराधिकार मिला है। इसमें संस्कृत व्याकरण की देन भी कम महत्त्वपूर्ण नहीं है। पं० किशोरीदास वाजपेयी ने लिखा है कि ''हिन्दी ने अपना व्याकरण प्रायः संस्कृत व्याकरण के आधार पर ही बनाया है- क्रियाप्रवाह एकान्त संस्कृत  व्याकरण के आधार पर है, पर कहीं-कहीं मार्गभेद भी है। मार्गभेद वहीं हुआ है, जहाँ हिन्दी ने संस्कृत की अपेक्षा सरलतर मार्ग ग्रहण किया है।''</p>   <h3>ध्वनि और लिपि</h3> <p><b>ध्वनि:-</b> ध्वनियाँ मनुष्य और पशु दोनों की होती हैं। कुत्ते का भूँकना और बिल्ली का म्याऊँ-म्याऊँ करना पशुओं के मुँह से निकली ध्वनियाँ हैं।  ध्वनि निर्जीव वस्तुओं की भी होती हैं। जैसे- जल का वेग, वस्तु का कम्पन आदि।</p> <p> व्याकरण में केवल मनुष्य के मुँह से निकली या उच्चरित ध्वनियों पर विचार किया जाता है। मनुष्यों द्वारा उच्चरित ध्वनियाँ कई प्रकार की होती हैं। एक तो वे,  जो मनुष्य के किसी क्रियाविशेष से निकलती हैं। जैसे- चलने की ध्वनि।</p> <p> दूसरी वे ध्वनियाँ हैं, जो मनुष्य की अनिच्छित क्रियाओं से उत्पत्र होती है; जैसे- खर्राटे लेना या जँभाई लेना। तीसरी वे हैं, जिनका उत्पादन मनुष्य के स्वाभाविक कार्यों द्वारा होता है; जैसे- कराहना। चौथी वे ध्वनियाँ हैं, जिन्हें मनुष्य अपनी इच्छा से अपने मुँह से उच्चरित करता है। इन्हें हम वाणी या आवाज कहते हैं।</p><p> पहली तीन प्रकार की ध्वनियाँ निरर्थक हैं। वाणी सार्थक और निरर्थक दोनों हो सकती है। निरर्थक वाणी का प्रयोग सीटी बजाने या निरर्थक गाना गाने में हो सकता है। सार्थक वाणी को भाषा या शैली कहा जाता है। इसके द्वारा हम अपनी इच्छाओं, धारणाओं अथवा अनुभवों को व्यक्त करते हैं। बोली शब्दों से बनती है और शब्द ध्वनियों के संयोग से।</p> <p> यद्यपि मनुष्य की शरीर-रचना में समानता है, तथापि उनकी बोलियों या भाषाओं में विभित्रता है। इतना ही नहीं, एक भाषा के स्थानीय रूपों में भी अन्तर पाया जाता है।  पर पशुओं की बोलियों में इतना अन्तर नहीं पाया जाता। मनुष्य की भाषा की उत्पत्ति मौखिक रूप से हुई। भाषाओं के लिखने की परिपाटी उनके निर्माण  के बहुत बाद आरम्भ हुई। यह तब हुआ, जब मनुष्य को अपनी भावनाओं, विचारों और विश्र्वासों को सुरक्षित रखने की प्रबल इच्छा महसूस हुई। </p>  <p>  आरम्भ में लिखने के लिए वाक्यसूचक चिह्नों से काम लिया गया और क्रमशः शब्दचिह्न और ध्वनिचिह्न बनने के बाद लिपियों का निर्माण हुआ।  चिह्नों में परिवर्तन होते रहे। वर्तमान लिपियाँ चिह्नों के अन्तिम रूप हैं। पर, यह कार्य अभी समाप्त नहीं हुआ है। उदाहरण के लिए, वर्तमान काल में हिन्दी लिपि में कुछ परिवर्तन करने का प्रयत्न किया जा रहा है। हिन्दी भाषा देवनागरी लिपि में लिखी जाती। है इसके अपने लिपि-चिह्न हैं। </p> <p><b>लिपि:-</b> मौखिक या कथित भाषा में ध्वनियाँ होती हैं, लिखित भाषा में उन ध्वनियों को विशेष आकारों या वर्णों द्वारा प्रकट किया जाता है। भाषा को लिखने का यह ढंग 'लिपि' है। हिन्दी भाषा की लिपि 'देवनागरी' है। अंग्रेजी 'रोमन लिपि' में तथा उर्दू 'पर्शियन' (फारसी) लिपि में लिखी जाती है। <h4><b> साहित्य </b></h4> <p> साहित्य की परिभाषा अनेक प्रकार से दी गई है। सामान्यतः हम कह सकते हैं कि सुन्दर अर्थ वाले सुन्दर शब्दों की रचना, जो मन को आह्रादित करे तथा समाज के लिए भी हितकारी हो, साहित्य कहलाती है। </p> <p> हिन्दी में कबीर, सूर, तुलसी, जयशंकर प्रसाद, निराला आदि कवियों तथा प्रेमचंद जैसे कहानीकार और उपन्यासकार की रचनाएँ साहित्य के उदाहरण रूप में ली जा सकती हैं। </p>",
    """
<p> <b><u>वर्ण(Phonology)- </b></u>वर्ण उस मूल ध्वनि को कहते हैं, जिसके खंड या टुकड़े नहीं किये जा सकते।<br>         <b>  इसे हम ऐसे भी कह सकते हैं-</b> वह सबसे छोटी ध्वनि जिसके और टुकड़े नहीं किए जा सकते,         वर्ण कहलाती है। </p><p> <b>दूसरे शब्दों में-</b> भाषा की सबसे छोटी इकाई ध्वनि है। और इस ध्वनि को वर्ण कहते है।  <br>            जैसे- अ, ई, व, च, क, ख् इत्यादि।</p>            <p>            वर्ण भाषा की सबसे छोटी इकाई है, इसके और खंड नहीं किये जा सकते। <br>            उदाहरण द्वारा मूल ध्वनियों को यहाँ स्पष्ट किया जा सकता है।            'राम' और 'गया' में चार-चार मूल ध्वनियाँ हैं, जिनके खंड नहीं किये जा सकते- र + आ + म + अ = राम, ग + अ + य + आ = गया।             इन्हीं अखंड मूल ध्वनियों को वर्ण कहते हैं। हर वर्ण की अपनी लिपि होती है। लिपि को वर्ण-संकेत भी कहते हैं। हिन्दी में 52 वर्ण हैं। </p>       <p><b><u>वर्णमाला(Alphabet)- </b></u> वर्णों के समूह को वर्णमाला कहते हैं।<br>         इसे हम ऐसे भी कह सकते है, किसी भाषा के समस्त वर्णो के  समूह को वर्णमाला कहते हैै। </p>         <p>प्रत्येक भाषा की अपनी वर्णमाला होती है। <br>  हिंदी- अ, आ, क, ख, ग..... <br>  अंग्रेजी- A, B, C, D, E.... </p>  <h3> वर्ण के भेद </h3>          <p>  हिंदी भाषा में वर्ण दो प्रकार के होते है-               <b>(1)स्वर (vowel)</b><b>  (2) व्यंजन (Consonant)</b>          </p>         <p>         <u><b>(1) स्वर (vowel) </u>:-</b> वे वर्ण जिनके उच्चारण में किसी अन्य वर्ण की सहायता की आवश्यकता नहीं होती,          स्वर कहलाता है।<br>          <b>दूसरे शब्दों में-</b> जिन वर्णों के उच्चारण में फेफ़ड़ों की वायु बिना रुके (अबाध गति से) मुख से निकल जाए,          उन्हें स्वर कहते हैं। <br>         इसके उच्चारण में कंठ, तालु का उपयोग होता है, जीभ, होठ का नहीं।        </p>        <p>    हिंदी वर्णमाला में 16 स्वर है<br>     जैसे- अ आ इ ई उ ऊ ए ऐ ओ औ अं अः ऋ ॠ ऌ ॡ।</p>                     <h4>  <u> स्वर के भेद </u> </h4>          <p>           स्वर के दो भेद होते है-<br>             <b>(i) मूल स्वर</b><b> (ii) संयुक्त स्वर</b></p>   <p> <b>(i) मूल स्वर:-</b> अ, आ, इ, ई, उ, ऊ, ए, ओ</p>   <p> <b>(ii) संयुक्त स्वर:-</b> ऐ (अ + ए) और औ (अ + ओ)</p>     <h4> <u>  मूल स्वर के भेद  </u></h4>     <p>      मूल स्वर के तीन  भेद होते है- <br>    <b>(i) ह्स्व स्वर</b><b> (ii) दीर्घ स्वर</b><b> (iii)प्लुत स्वर</b></p>        <p> <b>(i)ह्रस्व स्वर(Short Vowels):-</b> जिन स्वरों के उच्चारण में  कम समय लगता है उन्हें ह्स्व स्वर कहते है।<br>          ह्स्व स्वर चार होते है- अ आ उ ऋ।</p>     <p>  'ऋ' की मात्रा (ृ) के रूप में लगाई जाती है तथा उच्चारण 'रि' की तरह होता है। </p>            <p> <b>(ii)दीर्घ स्वर(Long Vowels):-</b> वे स्वर जिनके उच्चारण में ह्रस्व स्वर से दोगुना समय लगता है, वे दीर्घ स्वर कहलाते हैं। <br>         <b>सरल शब्दों में- </b>   स्वरों  उच्चारण में अधिक समय लगता है उन्हें दीर्घ स्वर कहते है। </p>                    <p> दीर्घ स्वर सात होते है- आ, ई, ऊ, ए, ऐ, ओ, औ। </p>                    <p> दीर्घ स्वर दो शब्दों के योग से बनते है।<br>                     जैसे- आ= (अ + अ)    <br>          ई= (इ + इ)     <br>        ऊ= (उ + उ) <br>                      ए= (अ + इ)<br>                           ऐ= (अ + ए)   <br>               ओ= (अ + उ)   <br>       औ= (अ + ओ) </p>              <p> <b>(iii)प्लुत स्वर:-</b> वे स्वर जिनके उच्चारण में दीर्घ स्वर से भी अधिक समय यानी तीन मात्राओं का समय लगता है, प्लुत स्वर कहलाते हैं।<br>           <b>सरल शब्दों में- </b>  जिस स्वर के उच्चारण में तिगुना समय लगे, उसे 'प्लुत' कहते हैं। </p>           <p> इसका चिह्न (ऽ) है। इसका प्रयोग अकसर पुकारते समय किया जाता है। जैसे- सुनोऽऽ, राऽऽम, ओऽऽम्। <br>हिन्दी में साधारणतः प्लुत का प्रयोग नहीं होता। वैदिक भाषा में               प्लुत स्वर का प्रयोग अधिक हुआ है। इसे  'त्रिमात्रिक' स्वर भी कहते हैं।</p>  </p> <h3><b><u>अयोगवाह </b></u></h3> <p> हिंदी वर्णमाला में ऐसे वर्ण जिनकी गणना न तो स्वरों में और न ही व्यंजनों में की जाती हैं। उन्हें अयोगवाह कहते हैं। </p> <p>अं, अः अयोगवाह कहलाते हैं। वर्णमाला में इनका स्थान स्वरों के बाद और व्यंजनों से पहले होता है। अं को अनुस्वार तथा अः को विसर्ग कहा जाता है।</p> <p> अयोगवाह चार प्रकार के होते हैं-<br>  <b>(1) अनुनासिक (ँ)</b><br>  <b>(2) अनुस्वार (ं)</b><br>  <b>(3) विसर्ग (ः)</b><br>  <b>(4) निरनुनासिक </b></p>  <p><b><u>(1)अनुनासिक (ँ)-</b></u> जिस ध्वनि के उच्चारण में हवा नाक और मुख दोनों से निकलती है उसे अनुनासिक कहते हैं।<br>  जैसे- गाँव, दाँत, आँगन, साँचा इत्यादि।</p>   <p><b><u>(2)अनुस्वार (ं)-</b></u> जिस वर्ण के उच्चारण में हवा केवल नाक से निकलती है। उसे अनुस्वार कहते हैं।<br>  जैसे- अंगूर, अंगद, कंकन। </p>   <p><b><u>(3)विसर्ग (ः)-</b></u> जिस ध्वनि के उच्चारण में 'ह' की तरह होता है। उसे विसर्ग कहते हैं।<br>  जैसे- मनःकामना, पयःपान, अतः, स्वतः, दुःख इत्यादि।</p>     <p><b><u>(4)निरनुनासिक-</b></u> केवल मुँह से बोले जानेवाला सस्वर वर्णों को निरनुनासिक कहते हैं। <br>जैसे- इधर,                   उधर, आप, अपना, घर इत्यादि। </p>                    <p><b>टिप्पणी</b>- अनुस्वार और विसर्ग न तो स्वर हैं, न व्यंजन; किन्तु ये स्वरों के सहारे चलते हैं।                      स्वर और व्यंजन दोनों में इनका उपयोग होता है।                      जैसे- अंगद, रंग। इस सम्बन्ध में आचार्य किशोरीदास वाजपेयी का कथन है कि  ''ये स्वर नहीं हैं                      और व्यंजनों की तरह ये स्वरों के                      पूर्व नहीं पश्र्चात आते हैं,                       ''इसलिए व्यंजन नहीं। इसलिए इन दोनों ध्वनियों को 'अयोगवाह' कहते हैं।'' अयोगवाह का अर्थ है- योग न होने                       पर भी जो साथ रहे।</p> <h5><b>                       अनुस्वार और अनुनासिक में अन्तर </b></h5>                        अनुनासिक के उच्चारण में नाक से बहुत कम साँस निकलती है और मुँह से अधिक, जैसे- आँसू, आँत, गाँव, चिड़ियाँ इत्यादि। <br>                        पर अनुस्वार के उच्चारण में नाक से अधिक साँस निकलती है और मुख से कम, जैसे- अंक, अंश, पंच, अंग इत्यादि। <br>                        अनुनासिक स्वर की विशेषता है, अर्थात अनुनासिक स्वरों पर चन्द्रबिन्दु लगता है। लेकिन, अनुस्वार एक व्यंजन ध्वनि है।<br>                        अनुस्वार की ध्वनि प्रकट करने के लिए वर्ण पर बिन्दु लगाया जाता है। तत्सम शब्दों में अनुस्वार लगता है और उनके तद्भव                        रूपों में चन्द्रबिन्दु लगता है; जैसे- अंगुष्ठ से अँगूठा, दन्त से दाँत, अन्त्र से आँत। </p>                         <h3>                        स्वरों की मात्राएँ (Vowel Signs)</h3> <p>                        'अ' के अतिरिक्त शेष स्वर जब व्यंजनों के साथ प्रयुक्त किए जाते हैं तो उनकी मात्राएँ ही लगती हैं।                        'अ' की मात्रा नहीं होती। अ से रहित व्यंजनों को हलंत लगाकर दिखाया जाता है। यथा- क्, ख्, ग्                        आदि। 'अ' लगने पर हलंत का चिह्न हट जाता है। क् + अ=क, ख् + अ=ख आदि। </p> <p>विभिन्न स्वरों की मात्राएँ और शब्दों में उनका प्रयोग देखिए-</p>   <table class="table table-bordered">     <thead>       <tr>         <th>  स्वर</th>         <th>मात्रा </th>         <th>शब्द-प्रयोग</th>       </tr>     </thead>     <tbody>    <tr>      <td> अ  </b> </td>      <td> - </td>      <td> -</td>     </tr>    <tr>      <td>  आ     </td>      <td>   ा </td>      <td>  राम </td>     </tr>    <tr>      <td>      इ </td>      <td>  ि </td>      <td> दिन </td>    </tr>    <tr>      <td>   ई</td>      <td>  ी </td>      <td> तीर </td>    </tr>    <tr>      <td>   ऋ </td>      <td>  ृ </td>      <td>  कृषक</td>    </tr>    <tr>      <td> उ  </td>      <td>   ु</td>      <td>  गुलाब</td>    </tr>    <tr>      <td> ऊ   </td>      <td>  ू </td>      <td>  फूल</td>    </tr>    <tr>      <td>   ए </td>      <td>  े </td>      <td>  केला</td>    </tr>    <tr>      <td>   ऐ </td>      <td>   ै</td>      <td>  मैना</td>    </tr>    <tr>      <td>    ओ</td>      <td> ो  </td>      <td>  कोमल</td>    </tr>    <tr>      <td> औ  </td>      <td> ौ  </td>      <td> सौरभ </td>    </tr>  </tbody> </table> <p>अं को बिन्दु ( ं ) तथा अः को विसर्ग ( ः) के रूप में लिखा जाता है।</p>        <p>   <u><b> (2) व्यंजन (Consonant)</u>:-</b> जिन वर्णो को बोलने के लिए स्वर की सहायता लेनी पड़ती है         उन्हें व्यंजन कहते है। <br>          दूसरे शब्दो में- व्यंजन उन वर्णों को कहते हैं, जिनके उच्चारण में स्वर वर्णों की सहायता ली जाती है। <br>         जैसे- क, ख, ग, च, छ, त, थ, द, भ, म इत्यादि। </p>         <p>         'क' से विसर्ग ( : ) तक सभी वर्ण व्यंजन हैं। प्रत्येक व्यंजन के उच्चारण में  'अ' की ध्वनि छिपी रहती है।  'अ' के बिना व्यंजन का उच्चारण सम्भव नहीं।         जैसे- ख्+अ=ख, प्+अ =प। व्यंजन वह ध्वनि है, जिसके उच्चारण में भीतर से आती हुई वायु मुख में कहीं-न-कहीं, किसी-न-किसी रूप में,          बाधित होती है। स्वरवर्ण स्वतंत्र और व्यंजनवर्ण स्वर पर आश्रित है। क् से ह् तक हिन्दी वर्णमाला में कुल 33 व्यंजन हैं। </p>                 <h3>   व्यंजन के प्रकार  </h3> <p>       व्यंजन   तीन  प्रकार के होते है-<br>         (1)स्पर्श व्यंजन(Mutes)<br> (2)अन्तःस्थ व्यंजन(Semivowels) <br>(3)उष्म या संघर्षी व्यंजन(Sibilants)  </p>          <p> <u><b>(1)स्पर्श व्यंजन(Mutes) </u>:-</b> स्पर्श का  अर्थ होता है -छूना। जिन व्यंजनों का उच्चारण करते           समय जीभ मुँह के किसी भाग             जैसे- कण्ठ, तालु, मूर्धा, दाँत, अथवा होठ का स्पर्श करती है, उन्हें स्पर्श व्यंजन कहते है।</p>             <p><b>सरल शब्दों में-</b> जिन व्यंजन वर्णो का उच्चारण करते समय वायु की टकराहट, कण्ठ, तालु, मूर्धा,               दन्त, ओष्ठ को छूती हुई निकले वो स्पर्श व्यंजन कहलाते है। </p>              <b>दूसरे शब्दो में-</b> ये कण्ठ, तालु, मूर्द्धा, दन्त और ओष्ठ स्थानों के स्पर्श से बोले जाते हैं।             इसी से इन्हें स्पर्श व्यंजन कहते हैं।<br>              इन्हें हम  'वर्गीय व्यंजन' भी कहते है;            क्योंकि ये उच्चारण-स्थान की अलग-अलग एकता लिए हुए वर्गों में विभक्त हैं।          </p>               <p><b>ये 25 व्यंजन होते है-</b> </p>                     <p>  <b>(1)कवर्ग- क   ख   ग    घ     ङ   </b>ये कण्ठ का स्पर्श करते है।</p>                            <p><b>(2)चवर्ग-  च   छ  ज  झ      ञ </b> ये तालु  का स्पर्श करते है।</p>                            <p><b> (3)टवर्ग- ट ठ ड ढ ण (ड़, ढ़)  </b> ये मूर्धा   का स्पर्श करते है। </p>                            <p><b> (4)तवर्ग- त थ द ध न        </b> ये दाँतो  का स्पर्श करते है।</p>                            <p><b> (5)पवर्ग-  प फ ब भ म          </b>  ये  होठों   का स्पर्श करते है।</p>               <p> <u><b>(2)अन्तःस्थ व्यंजन(Semivowels)  </u>:-</b>  'अन्तः' का अर्थ होता है- 'भीतर'। उच्चारण के समय जो व्यंजन मुँह के भीतर ही रहे उन्हें             अन्तःस्थ व्यंजन कहते है।</p>             <p><b>सरल शब्दों में- </b>जिन वर्गो का उच्चारण वर्णमाला के बीच अर्थात (स्वरों और व्यंजनों) के बीच होता हो,               वे अंतस्थ: व्यंजन कहलाते है। </p>      <p>अन्तः = मध्य/बीच, स्थ = स्थित। इन व्यंजनों का उच्चारण स्वर तथा व्यंजन के मध्य का-सा होता है। उच्चारण के समय       जिह्वा मुख के किसी भाग को स्पर्श नहीं करती।<br>             ये व्यंजन चार होते है-  य,  र,  ल,  व। इनका उच्चारण जीभ, तालु, दाँत और ओठों के परस्पर सटाने से होता है,            किन्तु कहीं भी पूर्ण स्पर्श नहीं होता। अतः ये चारों अन्तःस्थ व्यंजन  'अर्द्धस्वर' कहलाते हैं।          </p>             <p> <u><b> (3)उष्म या संघर्षी व्यंजन(Sibilants) </u>:-</b> उष्म का अर्थ होता है- गर्म। जिन वर्णो के उच्चारण के समय हवा मुँह के विभिन्न भागों से             टकराये और साँस में गर्मी पैदा कर दे, उन्हें उष्म व्यंजन कहते है।</p>             <p><b>दूसरे शब्दो में-</b> जिन व्यंजनों का उच्चारण करते समय वायु किसी स्थान विशेष पर घर्षण करती                हुई या रगड़ती हुई बाहर निकले जिससे गर्मी पैदा हो। उसे उष्म व्यंजन कहते हैं। </p>    <p>ऊष्म = गर्म। इन व्यंजनों के उच्चारण के समय वायु मुख से रगड़ खाकर ऊष्मा पैदा करती है यानी उच्चारण के समय मुख से गर्म हवा निकलती है।               <br>उष्म व्यंजनों का उच्चारण एक प्रकार की रगड़ या घर्षण से उत्पत्र उष्म वायु से होता हैं।<br>  ये भी चार  व्यंजन होते है- श, ष, स, ह।</p>
